mapper.update:
create temporary table d_track_info_tmp_#{timestamp}(sn varchar(20) primary key,updateTime datetime);
insert into d_track_info_tmp_#{timestamp} values 

   (#{trackInfo.sn},NOW())

;
update d_track_info t, d_track_info_tmp_#{timestamp} tmp set t.updateTime = tmp.updateTime where t.sn=tmp.sn;

INSERT INTO `d_track_info` (`trackerId`, sn,updateTime) VALUES

    (#{trackInfo.id},#{trackInfo.sn},NOW())

 on DUPLICATE key update updateTime=values(updateTime);
